    SET result = REPLACE(result, '"', '-');
    SET result = REPLACE(result, ' ', '-');

    -- Többszörös kötőjelek csökkentése
    WHILE INSTR(result, '--') > 0 DO
        SET result = REPLACE(result, '--', '-');
    END WHILE;

    RETURN result;
END$$

